int chk_num(int i, int str[], int j) {
    if (j == 0)
        return 0;
    if (i == str[j - 1])
        return 1;
    j = j - 1;
    return chk_num(i, str, j);
}

void fibonacci(int str[]) {
    int c, d, j;
    str[0] = 0;
    str[1] = 1;
    for (j = 2; j < 20; j++)
        str[j] = str[j - 2] + str[j - 1];
}

