#include <math.h>

double mod(double g)
{
   if(g>=0)
   return g;
   else 
   return (-1*g);
}

int main() 
{
    int i,n;
    double a,b,sum=0;
    scanf("%lf",&a);
    scanf("%lf",&b);
    scanf("%d",&n);
    double *x = (double *)malloc(n * sizeof(double));
    for(i=0;i<n;i++)
    {
        x[i]=a+(b*(i/(double)(n-1)));
    }
    for(i=0;i<n;i++)
    {
        printf("%lf\n",x[i]);
        sum=sum+((8*x[i])-(2*pow(x[i],3)))*((b-a)/n);
        sum=mod(sum);
    }
    printf("%.4lf",sum);
    free(x);
    return 0;
}

