double func(double x) {
    double ftn;
    if (x >= 0) {
        ftn = -3 * x * x * x + 3 * x + x * x + 5 * x;
    } else {
        ftn = -(-3 * x * x * x + 3 * x + x * x + 5 * x);
    }
    return ftn;
}

int main() {
    double a, b, c, sum;
    int n;

    scanf("%lf%lf", &a, &b);
    scanf("%d", &n);
    b > a;
    c = (b - a) / n;
    sum = 0;
    for (int i = a; i <= b; i++) {
        double t = a + c * i;
        sum += func(t) * c;
    }
    printf("%f", sum);

    return 0;
}

